import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import {
  Autocomplete,
  Button,
  FormControl,
  Grid,
  Stack,
  TextField,
  Typography,
  useFormControl,
} from "@mui/material";
import { CarT, ManufacturerT } from "../types";
import { capitalizeString } from "../helpers";

const API_URL = "http://localhost:8080/cars";
const MANUFACTURERS_URL =
  "https://private-anon-e2e1790a4a-carsapi1.apiary-mock.com/manufacturers";
const MODELS_URL =
  "https://private-anon-e2e1790a4a-carsapi1.apiary-mock.com/cars";

const CarList = (): JSX.Element => {
  const [cars, setCars] = useState<CarT[]>([]);
  const [manufacturers, setManufacturers] = useState<ManufacturerT[]>([]);
  const [selectedManufacturer, setManufacturer] = useState<
    string | ManufacturerT | null
  >();

  useEffect(() => {
    fetchCars();
    fetchManufacturers();
  }, []);

  useEffect(() => {
    fetchModels();
  }, [selectedManufacturer]);

  const fetchCars = async () => {
    try {
      const response = await axios.get<CarT[]>(API_URL);
      setCars(response.data);
    } catch (error) {
      console.error("Error fetching cars:", error);
    }
  };

  const fetchModels = async () => {
    try {
      const response = await axios.get<CarT[]>(MODELS_URL);
      const filteredModels = response.data.filter(
        (models) => models.make === selectedManufacturer?.label
      );
      setCars(response.data);
    } catch (error) {
      console.error("Error fetching cars:", error);
    }
  };

  const fetchManufacturers = async () => {
    try {
      const response = await axios.get(MANUFACTURERS_URL);
      const mappedManufacturers = response.data.map((man: ManufacturerT) => ({
        label: capitalizeString(man.name),
        id: man.id,
      }));
      console.log("response", response);
      setManufacturers(mappedManufacturers);
    } catch (error) {
      console.error("Error fetching cars:", error);
    }
  };

  return (
    <Stack display="flex" justifyContent="center" alignContent="center">
      <Grid
        display="flex"
        justifyContent="center"
        alignContent="center"
        alignItems="center"
      >
        <Typography variant="h4">Car List</Typography>
      </Grid>
      <Grid
        margin={5}
        columnGap={8}
        justifyContent="center"
        display="flex"
        alignItems="space-between"
      >
        <Stack display="flex" justifyContent="center">
          {cars.length
            ? cars.map((car) => (
                <div key={car.id}>
                  <Link to={`/cars/${car.id}`}>
                    <Typography>{`${car.make} ${car.model} (${car.year})`}</Typography>
                  </Link>
                </div>
              ))
            : null}
        </Stack>
        <Grid display="flex" justifyContent="center">
          <FormControl sx={{ width: "25ch" }}>
            <Autocomplete
              id="manufacturer"
              freeSolo
              disablePortal
              onChange={(
                event: any,
                newValue: ManufacturerT | null | string
              ) => {
                setManufacturer(newValue);
              }}
              options={manufacturers}
              renderInput={(params) => (
                <TextField
                  {...params}
                  id="outlined-basic"
                  label="Manufacturer"
                  variant="standard"
                  value={selectedManufacturer}
                  style={{ margin: 10 }}
                />
              )}
            />
            <Autocomplete
              id="manufacturer"
              freeSolo
              disablePortal
              options={manufacturers}
              renderInput={(params) => (
                <TextField
                  {...params}
                  // disabled={!manu}
                  id="outlined-basic"
                  label="Model"
                  variant="standard"
                  style={{ margin: 10 }}
                />
              )}
            />

            <TextField
              id="outlined-basic"
              label="Year"
              inputProps={{ inputMode: "numeric", pattern: "[0-9]*" }}
              variant="standard"
              style={{ margin: 10 }}
            />
            <Button variant="outlined">Submit</Button>
          </FormControl>
        </Grid>
      </Grid>
    </Stack>
  );
};

export default CarList;
